import React from 'react';
import ReactDOM from 'react-dom';

// App component has code to figure out user's location and month
class App extends React.Component {
    constructor(props) {
        super(props); // It must to be called!!!

        this.state = { lat: null }; // Initialize state object with sensible value, but THIS IS ONLY TIME we do direct assignment to this.state

        // Get users physical location
        window.navigator.geolocation.getCurrentPosition(
            (position) => this.setState({ lat: position.coords.latitude }), // To update state object, setState must be called! rather than "this.state.lat = value"
            (err) => console.log(err)
        );
    }

    // React says we have to define render!!
    render() {
        return <div>Latitude: { this.state.lat }</div>; // Refer to the state 
    }
}

ReactDOM.render(
    <App />,
    document.querySelector('#root')
);
